targets.expr:
sum by (job,instance,version) (
  label_replace(
  ciscoImageString{ciscoImageString=~"CW_VERSION.+", job=~"$job",instance=~"$instance"}, "version", "$1", "ciscoImageString", "CW_VERSION\\$(.+)\\$"
)

)
or ignoring(version)
sum by (job,instance,version) (
  label_replace(sysDescr{job=~"$job",instance=~"$instance"}, "version", "$1", "sysDescr", ".*Version ([0-9a-zA-Z\\.\\(\\)]+).*")
)

avg by (job,instance) (
  cpmCPUTotal1minRev{job=~"$job",instance=~"$instance"}
)
or
avg by (job,instance) (
  hrProcessorLoad{hrDeviceType="1.3.6.1.2.1.25.3.1.3",job=~"$job",instance=~"$instance"}
)
or
avg by (job,instance) (
  jnxOperatingCPU{jnxOperatingContentsIndex="9", job=~"$job",instance=~"$instance"}
)

topk(25,

  (
  sum by (job,instance,ifName,ifAlias,ifDescr) (
  irate(ifHCOutOctets{job=~"$job",instance=~"$instance",ifName=~"$ifName"} [$__rate_interval])
)
)*8

# set max limit to workaround for TB-PB/sec spikes when counters are overloaded too quickly on very busy interfaces.
<100*10^9
)

topk(25,

  (
  sum by (job,instance,ifName,ifAlias,ifDescr) (
  irate(ifHCInOctets{job=~"$job",instance=~"$instance",ifName=~"$ifName"}[$__rate_interval])
)
)*8

# set max limit to workaround for TB-PB/sec spikes when counters are overloaded too quickly on very busy interfaces.
<100*10^9
)

