targets.expr:
((avg by (job) (
  sum without (id) (jvm_memory_used_bytes{area="heap", job!="",job=~"$job",instance=~"$instance"})
))/(avg by (job) (
  sum without (id) (jvm_memory_max_bytes{area="heap", job!="",job=~"$job",instance=~"$instance"} != -1)
))) * 100

((avg by (job) (
  sum without (id) (jvm_memory_used_bytes{area="nonheap", job!="",job=~"$job",instance=~"$instance"})
))/(avg by (job) (
  sum without (id) (jvm_memory_max_bytes{area="nonheap", job!="",job=~"$job",instance=~"$instance"} != -1)
))) * 100
